import React, { useState } from "react";
import "./Filter.css";

const PriceFilter = () => {
  const [isPriceDropdownOpen, setIsPriceDropdownOpen] = useState(false);
  const [fromPrice, setFromPrice] = useState("");
  const [toPrice, setToPrice] = useState("");

  const togglePriceDropdown = () => {
    setIsPriceDropdownOpen(!isPriceDropdownOpen);
  };

  const handleApplyFilter = () => {
    // Implement your logic for applying the filter with the price range (fromPrice and toPrice)
    console.log(`Filter Applied: From ${fromPrice} to ${toPrice}`);
  };

  return (
    <div className="priceFilter">
      <label className="priceDropDown">
        Price
        <div className="price-dropdown" onClick={togglePriceDropdown}>
          {isPriceDropdownOpen ? "▲" : "▼"}
        </div>
      </label>
      <hr />
      {isPriceDropdownOpen && (
        <div>
          <div className="priceDropDownContent">
            <div className="fromInput">
              <label>
                From
                <input
                  type="text"
                  value={fromPrice}
                  onChange={(e) => setFromPrice(e.target.value)}
                />
              </label>
            </div>
            <div className="toInput">
              <label>
                To
                <input
                  type="text"
                  value={toPrice}
                  onChange={(e) => setToPrice(e.target.value)}
                />{" "}
              </label>
            </div>
          </div>
          <button onClick={handleApplyFilter}>Apply</button>
        </div>
      )}
    </div>
  );
};

export default PriceFilter;
