import React, { useState } from "react";
import "./Filter.css";
import Up from "../../assets/Filter/up.png";
import Down from "../../assets/Filter/down.png";
import BrandFilter from "./BrandFilter";
import SizeFilter from "./SizeFilter";

const PriceFilter = () => {
  const [isFilterExpanded, setIsFilterExpanded] = useState(false);
  const [isPriceDropdownOpen, setIsPriceDropdownOpen] = useState(true);
  const [fromPrice, setFromPrice] = useState("");
  const [toPrice, setToPrice] = useState("");

  const togglePriceDropdown = () => {
    setIsPriceDropdownOpen(!isPriceDropdownOpen);
  };

  const handleApplyFilter = () => {
    // Implement your logic for applying the filter with the price range (fromPrice and toPrice)
    console.log(`Filter Applied: From ${fromPrice} to ${toPrice}`);
  };

  return (
    <div className="priceFilter">
      <div className="priceHead">
        <p>Price</p>
        <div className="price-dropdown" onClick={togglePriceDropdown}>
          {isPriceDropdownOpen ? <img src={Up} /> : <img src={Down} />}
        </div>
      </div>

      <hr />
      {isPriceDropdownOpen && (
        <div className="priceRange">
          <div className="priceDropDownContent">
            <div className="fromInput">
              <p>From </p>
              <input
                className="inputPrice"
                type="text"
                value={fromPrice}
                onChange={(e) => setFromPrice(e.target.value)}
              />
            </div>
            <div className="toInput">
              <p>To</p>
              <input
                className="inputPrice"
                type="text"
                value={toPrice}
                onChange={(e) => setToPrice(e.target.value)}
              />{" "}
            </div>
          </div>
        </div>
      )}
      <BrandFilter />
      <SizeFilter />
      <div className="applyBtn">
        <button className="buyNowHomeBtn" onClick={handleApplyFilter}>
          Apply
        </button>
      </div>
    </div>
  );
};

export default PriceFilter;
