import React, { useState } from "react";
import Up from "../../assets/Filter/up.png";
import Down from "../../assets/Filter/down.png";
import { ProductData } from "../../data/ProductData";
import "./BrandFilter.css";
import { brandFilteredData } from "../Redux/Slice";
import { useDispatch } from "react-redux";

export default function BrandFilter() {
  const dispatch = useDispatch();
  const [isPriceDropdownOpen, setIsPriceDropdownOpen] = useState(true);
  const [brandName, setBrandName] = useState("");
  const [toPrice, setToPrice] = useState("");
  console.log(brandName);
  const togglePriceDropdown = () => {
    setIsPriceDropdownOpen(!isPriceDropdownOpen);
  };

  const handleApplyFilter = () => {
    const filteredBrandCondition = (ProductData) =>
      ProductData.name === brandName;
    const filteredBrand = () => ProductData.filter(filteredBrandCondition);
    dispatch(brandFilteredData(filteredBrand));
    console.log(`Filter Applied: From ${brandName} to ${toPrice}`);
  };

  return (
    <div className="BrandFilter">
      <div className="brandHead">
        <p>Brand</p>
        <div onClick={togglePriceDropdown}>
          {isPriceDropdownOpen ? <img src={Up} /> : <img src={Down} />}
        </div>
      </div>
      <hr />
      {isPriceDropdownOpen && (
        <div className="brandList">
          <label>
            <input
              className="check"
              type="checkbox"
              name="Apple"
              onChange={(e) => setBrandName(e.target.name)}
            />
            Apple
          </label>
          <label>
            <input className="check" type="checkbox" name="SamSung" />
            SamSung
          </label>
          <label>
            <input className="check" type="checkbox" name="Poco" />
            Poco
          </label>
          <label>
            <input className="check" type="checkbox" name="Oppo" />
            Oppo
          </label>
        </div>
      )}
    </div>
  );
}
