import React, { useEffect, useState } from "react";
import "./SmallerBanner.css";
import playStation from "../assets/PlayStation.png";
import playStationFull from "../assets/Banner/PlayStationFull.png";
import MacBook from "../assets/MacBook Pro 14mac.svg";
import Airpod from "../assets/headset.png";
import Vision from "../assets/vision.png";
export default function SmallerBanner() {
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth <= 460);
    };

    // Initial check on mount
    handleResize();

    // Listen for window resize events
    window.addEventListener("resize", handleResize);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);
  return (
    <div class="wrapper">
      <div class="box a">
        <img src={isSmallScreen ? playStationFull : playStation} />
        <div className="productContent">
          <p class="productName">Playstation 5</p>
          <p>
            Incredibly powerful CPUs, GPUs, and an SSD with integrated I/O will
            redefine your PlayStation experience.
          </p>
        </div>
      </div>
      <div class="box b">
        <div className="productContent">
          <p class="productName">Macbook Air</p>
          <p>
            The new 15â€‘inch MacBook Air makes room for more of what you love
            with a spacious Liquid Retina display.
          </p>
          <a className="macBtn">Shop Now</a>
        </div>
        <img src={MacBook} />
      </div>
      <div class="box c">
        <img src={Airpod} />
        <div className="productContent">
          <p class="productName">Apple AirPods Max</p>
          <p>Computational audio. Listen, it's powerful</p>
        </div>
      </div>
      <div class="box d">
        <img src={Vision} />
        <div className="productContent visioncontent">
          <p class="productName visionName">Apple Vision Pro</p>
          <p>An immersive way to experience entertainment</p>
        </div>
      </div>
    </div>
  );
}
